import { Card } from "react-bootstrap"
import { Component } from "react"
import CommentArea from "./CommentArea"
class SingleBook extends Component {
  state = {
    selected: false,
    bookSelectedAsin: ""
  }

  handleSelected = () => {
    if (this.state.selected === false) {
      this.setState({ selected: true, bookSelectedAsin: this.props.book.asin })
    } else {
      this.setState({ ...this.state, selected: false })
    }
  }

  render() {
    return (
      <>
        <Card
          // className="book-card my-2"
          // style={{
          //   border: this.state.selected ? "2px solid magenta" : ""
          // }}
          onClick={() => this.handleSelected()}
        >
          <Card.Img className="book-card-img" src={this.props.book.img} />
          <Card.Body>
            <Card.Title>{this.props.book.title}</Card.Title>
          </Card.Body>
        </Card>
        {this.state.selected && (
          <CommentArea elementId={this.props.book.asin} />
        )}
      </>
    )
  }
}

export default SingleBook
